console.log('==liuq debug== 本地Chart.js文件已加载');

// 简化的Chart.js实现 - 仅用于演示
if (typeof window !== 'undefined') {
    window.Chart = {
        version: '4.4.0-local',
        register: function() { console.log('Chart.register called'); },
        defaults: {},
        // 基本的Chart构造函数
        Chart: function(ctx, config) {
            console.log('==liuq debug== Chart构造函数被调用', ctx, config);
            this.ctx = ctx;
            this.config = config;
            this.data = config.data || {};
            this.options = config.options || {};
            
            // 简单的渲染逻辑
            this.render = function() {
                const canvas = ctx.canvas;
                ctx.clearRect(0, 0, canvas.width, canvas.height);
                ctx.fillStyle = '#f0f0f0';
                ctx.fillRect(0, 0, canvas.width, canvas.height);
                ctx.fillStyle = '#333';
                ctx.font = '16px Arial';
                ctx.textAlign = 'center';
                ctx.fillText('图表加载中...', canvas.width/2, canvas.height/2);
                ctx.fillText('(简化版本)', canvas.width/2, canvas.height/2 + 20);
            };
            
            // 立即渲染
            setTimeout(() => this.render(), 100);
            
            return this;
        }
    };
    
    // 为了兼容性，也设置全局Chart
    window.Chart = window.Chart.Chart;
    window.Chart.version = '4.4.0-local';
}
